import { Component, ElementRef, Renderer2, OnInit, HostListener } from '@angular/core';
@Component({
  selector: 'app-blog',
  templateUrl: './blog.component.html',
  styleUrls: ['./blog.component.css']
})
// export class BlogComponent {

