export class BlogComponent implements OnInit {
  constructor(private renderer: Renderer2, private elementRef: ElementRef) {}

  ngOnInit(): void {
    this.verificarAnchoPantalla();
  }

  @HostListener('window:resize', ['$event'])
  onResize(event: Event): void {
    this.verificarAnchoPantalla();
  }

  verificarAnchoPantalla(): void {
    const screenWidth = window.innerWidth;

    if (screenWidth < 1000) {
      this.quitarClase('col-9');
      this.aplicarClase('col-12');
    } else {
      this.aplicarClase('col-9');
      this.quitarClase('col-12');
    }
  }

  quitarClase(nombreClase: string): void {
    this.renderer.removeClass(this.elementRef.nativeElement, nombreClase);
  }

  aplicarClase(nombreClase: string): void {
    this.renderer.addClass(this.elementRef.nativeElement, nombreClase);
  }
}
