import { Component } from '@angular/core';
import { RestService } from '../../rest.service';

@Component({
  selector: 'app-render-blog',
  templateUrl: './render-blog.component.html',
  styleUrls: ['./render-blog.component.css']
})
export class RenderBlogComponent {

  constructor(private RestService: RestService) { }
  blog: any;

  ngOnInit(): void {
    this.loadBlog();
  }

  public loadBlog(): void {
    this.RestService.get('http://127.0.0.1:5001/blog/1').subscribe(data => {
      console.log(data);
      this.blog = data;
    });
  }

}
<div class="container mt-4">
    <div *ngIf="blog">
        <div class="row">
            <div class="col-md-12">
                <h1>{{ blog.title }}</h1>
                <img src="https://img.freepik.com/free-icon/user-image-with-black-background_318-34564.jpg?size=626&ext=jpg"
                    class="img-fluid rounded-circle" alt="Foto del autor" style="width: 50px; height: 50px;">
                <p><strong>Author: {{ blog.author }}</strong></p>
                <p>Date: {{ blog.date }}</p>
            </div>
        </div>
        <div class="row mt-4">
            <div class="col-md-6">
                <img src="https://images.pexels.com/photos/8438923/pexels-photo-8438923.jpeg" class="img-fluid"
                    alt="Imagen del artículo">
            </div>
            <div class="col-md-6">
                <div [innerHTML]="blog.content"></div>
            </div>
        </div>
    </div>
</div>

<script src="https://code.jquery.com/jquery-3.5.1.slim.min.js"></script>
<script src="https://cdn.jsdelivr.net/npm/@popperjs/core@2.5.4/dist/umd/popper.min.js"></script>
<script src="https://maxcdn.bootstrapcdn.com/bootstrap/4.5.2/js/bootstrap.min.js"></script>
