import { Component, Output, EventEmitter, OnInit } from '@angular/core';
import { SharedService } from '../../services/shared.service';
import { NewsService } from '../../services/news.service';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  @Output() parameters = new EventEmitter<any>();

  country: string = 'Colombia';
  category: string = 'inteligencia artificial';
  news:any;

  constructor(private sharedService: SharedService, private newsService: NewsService) { }

  ngOnInit(): void {
    this.searchNews();
    this.receiveNews();
    // this.sendParameters();
    // console.log('soy el hijo')
  }

  // sendParameters() {
  //   const datos = 'Hola desde el hijo';
  //   const params = {
  //     country: this.country,
  //     category: this.category
  //   }
  //   // this.parameters.emit(params);
  //   this.parameters.emit(datos);
  // }
  // Function to send the parameters to the parent component
  searchNews() {
    const params = {
      country: this.country,
      category: this.category
    }
    this.sharedService.sendDatos(params);
  }

  receiveNews() {
    this.newsService.getNews(this.category).then((data: any) => {
      this.news = data.articles;
      console.log(this.news);
    });

  }

}
